import React, { Component } from "react";
import { config } from "dotenv";
import styled from "styled-components";
import Wrapper from "./HOC/Wrapper";

const Button = styled.button`
  background: pink;
  border: 0.5px solid gray;
  padding: 5px;
  width: 80px;
  margin: 10px auto;
  display: block;
  font-size: 1.6rem;
  border-radius: 
`;

const Input = styled.input`
  width: 200px;
  border: 1px solid gray;
  display: block;
  padding: 15px;
  margin: 10px auto;
  border-radius: 4px;
  font-size: 18px;

  ::placeholder {
    color: teal;
  }

  :focus {
    outline: red;
  }
`;

class Api extends Component {
  state = {
    geolocationUrl: `https://api.mapbox.com/geocoding/v5/mapbox.places/Valera%20Trujillo.json?access_token=${
      process.env.DARKSKYAPITOKEN
    }`,
    darkskyUrl: `https://api.darksky.net/forecast/${
      process.env.darkskyApiToken
    }/${10.3},${10.1}`
  };

  componentDidMount() {
    console.log(this.state.geolocationUrl);
  }

  render() {
    return (
      <Wrapper>
        <h1>API page</h1>
        <Input placeholder="Enter location here" />
        <Button onClick={() => alert("Clicked")}>Hello</Button>
      </Wrapper>
    );
  }
}

export default Api;
