import React, { Component } from "react";
import axios from "axios";
import styled from "styled-components";
import Wrapper from "./HOC/Wrapper";
const geoApiToken =
  "pk.eyJ1IjoiZGllZ29tZmciLCJhIjoiY2p3eTU0b2pwMDBuZTQ5bzFzbnF1NnA3MSJ9.4PNeV8Pr0pye2xVU6VxIFw";
const darkApiToken = "5f92cde30212ee856099ea49f362a583";

const Button = styled.button`
  background: pink;
  border: 0.5px solid gray;
  padding: 5px;
  width: 80px;
  margin: 10px auto;
  display: block;
  font-size: 1.6rem;
  border-radius: 4px;
`;

const Input = styled.input`
  width: 200px;
  border: 1px solid gray;
  display: block;
  padding: 15px;
  margin: 10px auto;
  border-radius: 4px;
  font-size: 18px;

  ::placeholder {
    color: teal;
  }

  :focus {
    outline: red;
  }
`;

class Api extends Component {
  state = {
    city: null,
    geolocationUrl: `https://api.mapbox.com/geocoding/v5/mapbox.places/${encodeURI(
      "Doral Miami"
    )}.json?access_token=${geoApiToken}`,
    darkskyUrl: `https://api.darksky.net/forecast/${darkApiToken}/0,0`
  };

  setGeoUrl = () => {
    let cityToVisit = document.getElementById("input").value;
    let encoded = encodeURIComponent(cityToVisit);
    // not encoding the cityToVisit

    return new Promise((resolve, reject)=>{
      try {
        this.setState({
          geolocationUrl: `https://api.mapbox.com/geocoding/v5/mapbox.places/${encoded}.json?access_token=${geoApiToken}`
        });
      } catch (error) {

      }
    })
  };

  // NO CORS header. Request blocked
  fetchGeolocation = async event => {
    event.preventDefault();
    this.setGeoUrl();
    console.log(this.state.geolocationUrl);
    try {
      let response = await axios.get(this.state.geolocationUrl);
      const { center } = response.data.features[0];
      let latitude = center[1];
      let longitude = center[0];
      this.setState({
        darkskyUrl: `https://api.darksky.net/forecast/${darkApiToken}/${latitude},${longitude}`
      });

      this.fetchDarksky();
    } catch (error) {
      this.setState({ errorMsg: "Unable to connect to the service!" });
      console.log(error);
    }
  };

  fetchDarksky = async () => {
    try {
      let darksky = await axios.get(this.state.darkskyUrl);
      console.log(darksky);
    } catch (error) {
      this.setState({ errorMsg: "Unable to connect to the service!" });
      console.log("error fetchDarksky");
    }
  };

  render() {
    return (
      <Wrapper>
        <h1>API page</h1>
        <form onSubmit={this.fetchGeolocation}>
          <Input placeholder="Enter location here" id="input" />
          <Button>Hello</Button>
        </form>
      </Wrapper>
    );
  }
}

export default Api;
